targets.expr:
sum(rate(http_server_request_duration_seconds_sum{
  exported_job=~"$service_name", 
  http_request_method=~".*", 
  http_route=~".*", 
  http_route!~"/healthz|^$"
}[5m])) by (exported_job, http_request_method, http_route) * $__interval_ms / 1000

(
  sum(rate(http_server_request_duration_seconds_sum{
    exported_job=~"$service_name", 
    http_request_method=~".*", 
    http_route=~".*", 
    http_route!~"/healthz|^$"
  }[5m])) by (exported_job, http_request_method, http_route) 
  / 
  sum(rate(http_server_request_duration_seconds_count{
    exported_job=~"$service_name", 
    http_request_method=~".*", 
    http_route=~".*", 
    http_route!~"/healthz|^$"
  }[5m])) by (exported_job, http_request_method, http_route) 
  >= 0
) OR on() vector(0)
